import axios from '@/libs/api.request'

export const getPlayList = (pageSize,pageNum) => {
    return axios.request({
        url: 'PlayMsgs',
        method: 'get',
        params: {
            pageSize,
            pageNum
        }
    })
}

export const getPlayForOne = (req) => {
    return axios.request({
        url: 'PlayMsgs/' + req,
        method: 'get'
    })
}

export const updatePlay = (paramsstr, Id) => {
    const data = paramsstr
    return axios.request({
        url: 'PlayMsgs/' + Id,
        data,
        method: 'put'
    })
}

//添加
export const addPlay = (paramsstr) => {
    const data = paramsstr
    return axios.request({
        url: 'PlayMsgs',
        data,
        method: 'post'
    })
}

//删除
export const deletePlay = (req) => {
    return axios.request({
        url: 'PlayMsgs/' + req,
        method: 'delete'
    })
}
