import Tables from "_c/tables";
import { getTeam, deleteTeamTableData, getTeamForOne, updateTeam, addTeam } from "@/api/team";
export default {
  name: "playTypes_table",
  components: {
    Tables: Tables
  },
  data: function data() {
    var _this = this;

    return {
      formValidata: {},
      formModal: false,
      pageSize: 10,
      pageNum: 1,
      total: 0,
      columns: [{
        title: "ID",
        key: "Id",
        sortable: true,
        width: 80
      }, {
        title: "球队中文名",
        key: "ChineseName"
      }, {
        title: "球队英文名",
        key: "EnglishName"
      }, {
        title: "成立时间",
        key: "BuildTime"
      }, {
        title: "球场容量",
        key: "CourseCapacity",
        width: 70
      }, {
        title: "国家",
        key: "Country",
        width: 80
      }, {
        title: "地区",
        key: "Region",
        width: 80
      }, {
        title: "所在城市",
        key: "City",
        width: 70
      }, {
        title: "球队主场",
        key: "HomeTeam"
      }, {
        title: "球队身价",
        key: "TeamValue",
        width: 70
      }, {
        title: "球队logo",
        key: "TeamLogo"
      }, {
        title: "添加时间",
        key: "AddTime"
      }, {
        title: "编辑时间",
        key: "EditTime"
      }, {
        title: "操作",
        key: "action",
        width: 150,
        align: "center",
        fixed: "right",
        render: function render(h, params) {
          return h("div", [h("Button", {
            props: {
              type: "primary",
              size: "small"
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: function click() {
                _this.setdata(params);
              }
            }
          }, "查看"), h("Button", {
            props: {
              type: "error",
              size: "small"
            },
            on: {
              click: function click() {
                _this.handleDelete(params.index, params.row.Id);
              }
            }
          }, "删除")]);
        }
      }],
      tableData: []
    };
  },
  methods: {
    //更新表格
    updataModel: function updataModel() {
      var _this2 = this;

      getTeam(this.pageSize, this.pageNum).then(function (res) {
        _this2.tableData = res.data.Data.List;
        _this2.total = res.data.Data.Count;
      });
    },
    handleDelete: function handleDelete(index, Id) {
      var _this3 = this;

      deleteTeamTableData(Id.toString()).then(function (res) {
        if (res.data.Status == 1) {
          _this3.$Message.success("删除成功！");
        } else {
          _this3.$Message.error("删除失败！");
        }
      });
      this.tableData.splice(index, 1);
    },
    exportExcel: function exportExcel() {
      this.$refs.tables.exportCsv({
        filename: "table-".concat(new Date().valueOf(), ".csv")
      });
    },
    //编辑
    setdata: function setdata(params) {
      var _this4 = this;

      getTeamForOne(params.row.Id.toString()).then(function (res) {
        if (res.data.Status == 1) {
          _this4.formValidata = res.data.Data;
        } else {
          _this4.$Message.error("获取信息失败！");
        }
      });
      this.formModal = true;
    },
    //弹出框确认
    ok: function ok() {
      var _this5 = this;

      if (this.formValidata.Id != null && this.formValidata.Id != "") {
        updateTeam(this.formValidata, this.formValidata.Id).then(function (res) {
          if (res.data.Status == 1) {
            _this5.$Message.success("更新信息成功！");
          } else {
            _this5.$Message.error("更新信息失败！");
          }

          _this5.updataModel();
        });
      } else {
        addTeam(this.formValidata).then(function (res) {
          if (res.data.Status == 1) {
            _this5.$Message.success("添加信息成功！");
          } else {
            _this5.$Message.error("添加信息失败！");
          }

          _this5.updataModel();
        });
      }

      this.formModal = false;
    },
    //弹出框取消
    cancel: function cancel() {
      this.$Message.info("编辑取消！");
    },
    //添加编辑
    addModel: function addModel() {
      this.formValidata = {};
      this.formModal = true;
    },
    PageChange: function PageChange(PageNum) {
      this.pageNum = PageNum;
      this.updataModel();
    },
    HandlePageSize: function HandlePageSize(PageSize) {
      this.pageSize = PageSize;
      this.updataModel();
    }
  },
  mounted: function mounted() {
    this.updataModel();
  }
};
