import axios from '@/libs/api.request'

export const getTeam = (pageSize, pageNum) => {
    return axios.request({
        url: 'Teambasemsgs',
        method: 'get',
        params: {
            pageSize,
            pageNum
        }
    })
}

//获取单个球队信息 匹配ID
export const getTeamForOne = (req) => {
    return axios.request({
        url: 'Teambasemsgs/' + req,
        method: 'get'
    })
}

//更新单条数据
export const updateTeam = (paramsstr, Id) => {
    const data = paramsstr
    return axios.request({
        url: 'Teambasemsgs/' + Id,
        data,
        method: 'put'
    })
}

//添加球队数据
export const addTeam = (paramsstr) => {
    const data = paramsstr
    return axios.request({
        url: 'Teambasemsgs',
        data,
        method: 'post'
    })
}

//删除球队信息
export const deleteTeamTableData = (req) => {
    return axios.request({
        url: 'Teambasemsgs/' + req,
        method: 'delete'
    })
}
