import Tables from "_c/tables";
import {
  TypeThirdsList,
  TypeThirdsForOne,
  updateTypeThirds,
  addTypeThirds,
  deleteTypeThirds
} from "@/api/playTypes";
export default {
  name: "playTypes_table",
  components: {
    Tables
  },
  data() {
    return {
      formValidata: {},
      formModal: false,
      pageSize: 10,
      pageNum: 1,
      total: 0,
      columns: [
        { title: "ID", key: "TypeThirdId", sortable: true },
        { title: "一级赛事名称", key: "TypeThirdName" },
        { title: "排序", key: "TypeThirdSort" },
        {
          title: "操作",
          key: "action",
          width: 150,
          align: "center",
          fixed: "right",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.setdata(params);
                    }
                  }
                },
                "查看"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "error",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.handleDelete(params.index, params.row.Id);
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      tableData: []
    };
  },
  methods: {
    //更新表格
    updataModel() {
      TypeThirdsList(this.pageSize, this.pageNum).then(res => {
        this.tableData = res.data.Data.List;
        this.total = res.data.Data.Count;
      });
    },
    handleDelete(index, Id) {
      deleteTypeThirds(Id.toString()).then(res => {
        if (res.data.Status == 1) {
          this.$Message.success("删除成功！");
        } else {
          this.$Message.error("删除失败！");
        }
      });
      this.tableData.splice(index, 1);
    },
    exportExcel() {
      this.$refs.tables.exportCsv({
        filename: `table-${new Date().valueOf()}.csv`
      });
    },
    //编辑
    setdata(params) {
      TypeThirdsForOne(params.row.TypeThirdId.toString()).then(res => {
        if (res.data.Status == 1) {
          this.formValidata = res.data.Data;
        } else {
          this.$Message.error("获取信息失败！");
        }
      });
      this.formModal = true;
    },
    //弹出框确认
    ok() {
      if (this.formValidata.TypeThirdId != null && this.formValidata.TypeThirdId != "") {
        updateTypeThirds(this.formValidata, this.formValidata.TypeThirdId).then(res => {
          if (res.data.Status == 1) {
            this.$Message.success("更新信息成功！");
          } else {
            this.$Message.error("更新信息失败！");
          };
          this.updataModel();
        });
      } else {
        addTypeThirds(this.formValidata).then(res => {
          if (res.data.Status == 1) {
            this.$Message.success("添加信息成功！");
          } else {
            this.$Message.error("添加信息失败！");
          };
          this.updataModel();
        });        
      }
      this.formModal = false;      
    },
    //弹出框取消
    cancel() {
      this.$Message.info("编辑取消！");
    },
    //添加编辑
    addModel() {
      this.formValidata = {};
      this.formModal = true;
    },    
    PageChange(PageNum) {
      this.pageNum = PageNum;
      this.updataModel();
    },
    HandlePageSize(PageSize) {
      this.pageSize = PageSize;
      this.updataModel();
    }
  },
  mounted() {
    this.updataModel();
  }
};
