import Tables from "_c/tables";
import {
  getPlayList,
  getPlayForOne,
  updatePlay,
  addPlay,
  deletePlay
} from "@/api/play";
import {
  getPlayTypeFirstList,
  getPlayTypeSecondList,
  getGetPlayTypeThirdList,
  getTeamList
} from "@/api/getList";
import Selectdata from "@/api/selectdata";
export default {
  name: "playTypes_table",
  components: {
    Tables
  },
  data() {
    return {
      PlayTypeFirst_List: [],
      PlayTypeSecond_List: [],
      PlayTypeThird_List: [],
      formValidata: {},
      CompetitionStatus_List: Selectdata.CompetitionStatus_List,
      formModal: false,
      pageSize: 10,
      pageNum: 1,
      total: 0,
      columns: [
        { title: "ID", key: "playId", sortable: true },
        { title: "A队伍名", key: "teamAName" },
        { title: "A队得分", key: "teamAPoint" },
        { title: "B队伍名", key: "teamBName" },
        { title: "B队得分", key: "teamBPoint" },
        { title: "一级赛事名称", key: "typeFirstName" },
        { title: "二级赛事名称", key: "typeSecondName" },
        { title: "三级赛事名称", key: "typeThirdName" },
        // { title: "比赛类型", key: "playType" },
        { title: "比赛状态", key: "competitionStatus" },
        { title: "比赛开始时间", key: "playTimeStart" },
        { title: "比赛结束时间", key: "playTimeEnd" },
        // { title: "队伍A", key: "teamAId"},
        // { title: "队伍B", key: "teamBId"},
        { title: "直播地址1", key: "liveAddress1" },
        { title: "直播地址2", key: "liveAddress2" },
        { title: "直播地址3", key: "liveAddress3" },
        { title: "直播地址4", key: "liveAddress4" },
        { title: "直播地址5", key: "liveAddress5" },
        {
          title: "操作",
          key: "action",
          width: 150,
          align: "center",
          fixed: "right",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.setdata(params);
                    }
                  }
                },
                "查看"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "error",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.handleDelete(params.index, params.row.Id);
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      tableData: []
    };
  },
  methods: {
    //更新表格
    updataModel() {
      getPlayList(this.pageSize, this.pageNum).then(res => {
        this.tableData = res.data.Data.List;
        this.total = res.data.Data.Count;
      });
    },
    handleDelete(index, Id) {
      deletePlay(Id.toString()).then(res => {
        if (res.data.Status == 1) {
          this.$Message.success("删除成功！");
        } else {
          this.$Message.error("删除失败！");
        }
      });
      this.tableData.splice(index, 1);
    },
    exportExcel() {
      this.$refs.tables.exportCsv({
        filename: `table-${new Date().valueOf()}.csv`
      });
    },
    //编辑
    setdata(params) {
      getPlayForOne(params.row.playId.toString()).then(res => {
        if (res.data.Status == 1) {
          this.formValidata = res.data.Data;
          console.log(this.formValidata);
        } else {
          this.$Message.error("获取信息失败！");
        }
      });
      this.formModal = true;
    },
    //弹出框确认
    ok() {
      if (this.formValidata.PlayId != null && this.formValidata.PlayId != "") {
        updatePlay(this.formValidata, this.formValidata.PlayId).then(res => {
          if (res.data.Status == 1) {
            this.$Message.success("更新信息成功！");
          } else {
            this.$Message.error("更新信息失败！");
          }
          this.updataModel();
        });
      } else {
        addPlay(this.formValidata).then(res => {
          if (res.data.Status == 1) {
            this.$Message.success("添加信息成功！");
          } else {
            this.$Message.error("添加信息失败！");
          }
          this.updataModel();
        });
      }
      this.formModal = false;
    },
    //弹出框取消
    cancel() {
      this.$Message.info("编辑取消！");
    },
    //添加编辑
    addModel() {
      this.formValidata = {};
      this.formModal = true;
    },
    PageChange(PageNum) {
      this.pageNum = PageNum;
      this.updataModel();
    },
    HandlePageSize(PageSize) {
      this.pageSize = PageSize;
      this.updataModel();
    }
  },
  mounted() {
    this.updataModel();
    getPlayTypeFirstList().then(res => {
      this.PlayTypeFirst_List = res.data.Data;
    });
    getPlayTypeSecondList().then(res => {
      this.PlayTypeSecond_List = res.data.Data;
    });
    getGetPlayTypeThirdList().then(res => {
      this.PlayTypeThird_List = res.data.Data;
    });
    // getTeamList().then(res => {
    //   this.Team_List = res.data.Data;
    // });
  }
};
