import Tables from "_c/tables";
import {
  getPlayTypeList,
  getPlayTypeForOne,
  updatePlayType,
  addPlayType,
  deletePlayType
} from "@/api/playTypes";
export default {
  name: "basketball_table",
  components: {
    Tables
  },
  data() {
    return {
      formValidata: {},
      formModal: false,
      columns: [
        { title: "ID", key: "TypeId", sortable: true },
        { title: "栏目名称", key: "TypeName" },
        { title: "排序序号", key: "Sort" },
        { title: "添加时间", key: "AddTime" },
        {
          title: "操作",
          key: "action",
          width: 150,
          align: "center",
          fixed: "right",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.setdata(params);
                    }
                  }
                },
                "查看"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "error",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.handleDelete(params.index, params.row.Id);
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      tableData: []
    };
  },
  methods: {
    handleDelete(index, Id) {
      deleteTeamTab(Id.toString()).then(res => {
        if (res.data.Status == 1) {
          this.$Message.success("删除成功！");
        } else {
          this.$Message.error("删除失败！");
        }
      });
      this.tableData.splice(index, 1);
    },
    exportExcel() {
      this.$refs.tables.exportCsv({
        filename: `table-${new Date().valueOf()}.csv`
      });
    },
    //编辑
    setdata(params) {
      getTabListForOne(params.row.TypeId.toString()).then(res => {
        if (res.data.Status == 1) {
          this.formValidata = res.data.Data;
        } else {
          this.$Message.error("获取信息失败！");
        }
      });
      this.formModal = true;
    },
    //弹出框确认
    ok() {
      if (this.formValidata.TypeId != null && this.formValidata.TypeId != "") {
        updateTab(this.formValidata, this.formValidata.TypeId).then(res => {
          if (res.data.Status == 1) {
            this.$Message.success("更新信息成功！");
          } else {
            this.$Message.error("更新信息失败！");
          }
        });
        updata();
      } else {
        addTab(this.formValidata).then(res => {
          if (res.data.Status == 1) {
            this.$Message.success("添加信息成功！");
          } else {
            this.$Message.error("添加信息失败！");
          }
        });
        updata();
      }
      this.formModal = false;
    },
    //弹出框取消
    cancel() {
      this.$Message.info("编辑取消！");
    },
    //添加编辑
    addModel() {
      this.formValidata = {};
      this.formModal = true;
    },
    //更新表格
    updata() {
      getTabList().then(res => {
        this.tableData = res.data.Data;
      });
    }
  },
  mounted() {
    getTabList().then(res => {
      this.tableData = res.data.Data;
    });
  }
};
